using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace DeploySoftware.LaunchPad.Core.FileGeneration
{
    /// <summary>
    /// Represents a set of related documents (Office document (Word, Excel, Powerpoint, RTF) generated by LaunchPad Framework.
    /// </summary>    
    [Serializable]
    public partial class LaunchPadGeneratedDocumentSetModule :
        LaunchPadGeneratedModule<LaunchPadGeneratedDocumentSetModuleConfiguration,
            LaunchPadGeneratedComponent<LaunchPadGeneratedConfiguration>,
            LaunchPadGeneratedConfiguration>
    {

        /// <summary>
        /// The list of folders that belong to this module.
        /// </summary>
        public virtual IList<LaunchPadGeneratedFolder> Folders { get; set; }

        /// <summary>
        /// The list of documents that belong to this module.
        /// </summary>
        public virtual IList<LaunchPadGeneratedDocument> Documents { get; set; }

        public LaunchPadGeneratedDocumentSetModule() : base()
        {
            Folders = new List<LaunchPadGeneratedFolder>(); 
            Documents = new List<LaunchPadGeneratedDocument>();
        }

    }
}
