using System;
using System.Collections.Generic;

namespace DeploySoftware.LaunchPad.Core.FileGeneration
{
    /// <summary>
    /// Represents a solution generated by LaunchPad Framework which contains dynamic (angular) web resources.
    /// </summary>
    [Serializable]
    public partial class LaunchPadGeneratedWebClientSolution : LaunchPadGeneratedStaticWebSolution
    {

        /// <summary>
        /// The set of generated dynamic web client modules that belong to this solution.
        /// </summary>
        public virtual IDictionary<string, LaunchPadGeneratedWebClientModule> WebClientModules { get; set; }

        public LaunchPadGeneratedWebClientSolution() : base()
        {
            var comparer = StringComparer.OrdinalIgnoreCase;
            WebClientModules = new Dictionary<string, LaunchPadGeneratedWebClientModule>(comparer);
        }
    }
}
