using System.Collections.Generic;

namespace DeploySoftware.LaunchPad.Core.Util
{
    /// <summary>
    /// Represents an application generated by LaunchPad Framework.
    /// </summary>
    public partial class LaunchPadGeneratedApplication : LaunchPadGeneratedObjectBase
    {
       
        /// <summary>
        /// The version of this module
        /// </summary>
        public string Version { get; set; }

        /// <summary>
        /// The list of generated modules that belong to this application.
        /// </summary>
        public IList<LaunchPadGeneratedModule> Modules { get; set; }

        public LaunchPadGeneratedApplication() : base()
        {
            Version = string.Empty;
            Modules = new List<LaunchPadGeneratedModule>();
        }
    }
}
